  width: 100%;
  height: 171px;
  left: 0;
  top: 0;
  background-color: #fff5f6;
  position: absolute;
}

.images-wrapper li:not(:last-child) {
  margin-right: 8px;
}

.images-wrapper-list {
  display: flex;
  justify-content: center;
  align-items: center;
  padding-left: 20px;
  padding-right: 20px;
  position: relative;
}

.images-wrapper-list-four {
  margin-top: 45px;
}

@media screen and (max-width: 479px) {
  .images-wrapper-list {
    max-width: 287px;
    height: 100%;
  }
}
@media screen and (max-width: 767px) {
  .images-wrapper-list {
    max-width: 620px;
    height: 100%;
  }
}
@media screen and (min-width: 768px) and (max-width: 1199px) {
  .images-wrapper-list {
    width: auto;
    height: 100%;
  }
}
@media screen and (max-width: 1199px) {
  .images-wrapper-list {
    max-width: 1010px;
    height: 100%;
  }
}
.feedback {
  padding-bottom: 100px;
  background-color: #FFFFFF;
}

.feedback-container {
  margin: 0 auto;
  padding-left: 20px;
  padding-right: 20px;
}

.feedback-photo {
  margin-left: auto;
  margin-right: auto;
}

.feedback-description {
  position: relative;
  background-color: #FAFAFA;
}

.feedback-text, .feedback-text-name {
  text-align: center;
  align-items: center;
  justify-content: center;
  font-family: DM Sans;
  font-style: normal;
  font-weight: 500;
  font-size: 12px;
  line-height: 2.55;
  color: #000000;
}

.feedback-wrapper-for-text {
  position: relative;
  background-color: transparent;
}

.feedback-text {
  position: relative;
  /*     border: 1px solid red; */
  z-index: 1;
  text-decoration-style: solid;
  text-decoration-color: #E1E1E1;
}

.feedback-stripes {
  position: absolute;
  top: 28.5px;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to bottom, #E1E1E1, #E1E1E1 1px, #FAFAFA 1px, #FAFAFA);
  background-size: 100% 30px;
}

.feedback-quotes {
  position: absolute;
  fill: #E1E1E1;
  width: 54px;
  height: 40px;
}

.feedback-circle {
  display: flex;
  align-items: center;
  justify-content: center;
}

.feedback-circle-icon {
  border-radius: 50%;
  width: 4px;
  height: 4px;
  background: #C7133E;
}
.feedback-circle-icon:not(:last-child) {
  margin-right: 10px;
}

.feedback-icons {
  display: flex;
  justify-content: center;
  align-items: center;
  padding-left: 0px;
  margin-bottom: 0px;
}

.feedback-icons-label:not(:last-child) {
  margin-right: 18px;
}

.feedback-icon-list-svg {
  width: 12px;
  height: 12px;
  fill: #D31342;
}

.feedback-icon-list-background {
  display: block;
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: #D9D9D9;
}

@media screen and (min-width: 320px) and (max-width: 767px) {
  .feedback-photo {
    margin-bottom: 29px;
  }

  .feedback-description {
    margin-left: auto;
    margin-right: auto;
    padding: 43px 66px 26px 57px;
    width: 280px;
    height: 308px;
  }

  .feedback-quotes {
    top: 28px;
    left: 61px;
  }

  .feedback-text {
    letter-spacing: 0;
    border-bottom: unset;
  }

  .feedback-circle {
    margin-top: 30px;
  }

  .feedback-text-name {
    margin-top: 18px;
    font-size: 14px;
    line-height: 1.86;
  }

  .feedback-icons {
    margin-top: 30px;
  }
}
@media screen and (min-width: 768px) {
  .feedback-container {
    width: 768px;
    padding-left: 34px;
    padding-right: 34px;
  }

  .feedback-photo {
    margin-bottom: 19px;
  }

  .feedback-description {
    margin-left: auto;
    margin-right: auto;
    padding: 47px 80px 34px 80px;
    width: 620px;
    height: 164px;
  }

  .feedback-quotes {
    z-index: 1;
    top: 30px;
    left: 40px;
  }

  .feedback-text {
    font-size: 14px;
    line-height: 2.16;
    letter-spacing: 0.04em;
    border-bottom: unset;
  }

  .feedback-circle {
    margin-top: 24px;
  }

  .feedback-text-name {
    margin-top: 14px;
    font-size: 16px;
    line-height: 1.86;
  }

  .feedback-icons {
    margin-top: 35px;
  }
}
@media screen and (min-width: 1280px) {
  .feedback-container {
    width: 1300px;
    padding-left: 115px;
    padding-right: 115px;
  }

  .feedback-description {
    margin-left: auto;
    margin-right: auto;
    padding: 47px 88px 39px 88px;
    width: 926px;
    height: 138px;
  }

  .feedback-quotes {
    top: 32px;
    left: 50px;
  }

  .feedback-text {
    font-size: 16px;
    line-height: 1.86;
    border-bottom: 1px solid #E1E1E1;
  }

  .feedback-circle {
    margin-top: 15px;
  }

  .feedback-text-name {
    margin-top: 15px;
    font-size: 20px;
    line-height: 1.86;
  }

  .feedback-photo {
    margin-bottom: 15px;
  }

  .feedback-icons {
    margin-top: 35px;
  }
}
footer {
  background-color: #fff5f6;
}

.footer {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  padding: 97px 0;
}

.footer-text-rose {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 30px;
  line-height: 1.133;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffa5ba;
  margin-bottom: 15px;
}

.footer-text-red {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 58px;
  line-height: 1.137;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  color: #d41443;
}

.footer-tel {
  margin-top: 50px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  letter-spacing: 0.02em;
  color: #000000;
}
.footer-tel a {
  color: #d41443;
  text-decoration: none;
}

.footer-social-icons ul {
  list-style: none;
  display: flex;
  padding: 0;
  justify-content: flex-end;
}
.footer-social-icons ul li:not(:last-child) {
  margin-right: 24px;
}
.footer-social-icons ul a {
  border-radius: 50%;
  background: #fea5bb;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 34px;
  height: 34px;
  transition: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 250ms;
}
.footer-social-icons ul a svg {
  fill: #fff;
}
.footer-social-icons ul a:hover {
  background: #d41443;
}

.footer-copyright {
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  letter-spacing: 0.02em;
  color: #907e82;
  text-align: center;
  border-top: 1px solid #e1e1e1;
  padding: 10px 0;
}

@media screen and (max-width: 767px) {
  .footer {
    flex-direction: column;
    padding: 113px 0 0 0;
  }

  .footer-text {
    padding: 0;
  }

  .footer-text-rose {
    padding-right: 66px;
    font-size: 18px;
    line-height: 1.166;
  }

  .footer-text-red {
    padding-bottom: 43px;
    font-size: 34px;
    line-height: 1.147;
    letter-spacing: 0.04em;
  }
  .footer-text-red span {
    display: block;
  }

  .footer-social-icons {
    width: 100%;
  }
  .footer-social-icons ul li:not(:last-child) {
    margin-right: 28.24px;
  }
  .footer-social-icons ul a {
    width: 32px;
    height: 32px;
  }

  .footer-tel {
    font-size: 12px;
    padding-bottom: 43px;
    text-align: right;
  }

  .footer-copyright {
    padding: 22px 0 25px 0;
  }
  .footer-copyright span {
    display: block;
  }
}
@media screen and (min-width: 768px) and (max-width: 1279px) {
  .footer {
    padding: 91px 0;
    align-items: start;
  }

  .footer-text-rose {
    font-size: 26px;
    line-height: 1.153;
    letter-spacing: 0.04em;
  }

  .footer-text-red {
    font-size: 48px;
    line-height: 1.145;
    letter-spacing: 0.06em;
  }
  .footer-text-red span {
    display: block;
  }

  .footer-tel {
    font-size: 14px;
  }

  .footer-copyright {
    padding-top: 29px;
    padding-bottom: 18px;
  }
}

sources.sass\\main.scss:
/*
 * Главный файл стилей.
 * Все паршалы подключаются сюда.
 */

@import './global-styles/variables';
@import './global-styles/fonts';
@import './global-styles/global-styles';

@import './layout/header';
@import './layout/hero';
@import './layout/products';
@import './layout/how-its-made';
@import './layout/points-of-sale';
@import './layout/images';
@import './layout/feedback';
@import './layout/footer';

sources.sass\\global-styles\\_global-styles.scss:
html { 
    box-sizing: border-box;
    overflow-x:  hidden;
    min-width: 320px;
}

*,
*::before,
*::after {
    box-sizing: inherit;
}

body{
    margin: 0;
}

.section-wrapper{
    /* Тянется на всю ширину! */
    width: 100%;
}

ul{
    list-style: none;
    margin: 0;
    padding: 0;
}

h1{
    font-family: $h-font;
    margin:0;
    font-weight: normal;
    text-transform: uppercase;
}

img {
    display: block;
    max-width: 100%;
    height: auto;
}

p {
    margin-top: 0px;
    margin-bottom: 0px;
}

    .container{
        position: relative;
        margin: 0 auto;
        padding-left: 20px;
        padding-right: 20px;

        @media screen and (min-width: 768px){
            width: 775px;
            padding-left: 34px;
            padding-right: 34px;
        }
        @media screen and (min-width: 1280px){
            width: 1300px;
            padding-left: 115px;
            padding-right: 115px;
        }
    }

.button{
    display: block;
    height: 44px;
    border: none;
    padding-left: 21px;
    padding-right: 21px;
    border-radius: 20px; 
    font-size: 14px;
    font-family: DM Sans;
    font-weight: bold;
    line-height: 18px;
    cursor: pointer;
    
    &-light{
        background-color: #ffffff;
        color: #D41443;
    }
    &-light:hover{
        background-color: #ECEBEB;
        color: #D41443;
    }

    &-dark{
        background-color: #D41443;
        color: #ffffff;
    }

    &-dark:hover{
        background-color: #C7133E;
        color: #ffffff
    }

    &-arrow{
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 16px;
        padding-left: 37px;
        padding-right: 37px;
        .button-title{
            display: block;
            margin-right: 12px;
        }
    }
}

.section-sub-title {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 30px;
  line-height: 34px;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffa5ba;
}

.section-title {
  font-family: Titan One;
  font-weight: normal;
  font-size: 58px;
  line-height: 66px;
  text-align: center;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  color: #d41443;
}

.hero-title-start {
  font-family: Titan One;
  font-weight: normal;
  font-size: 38px;
  line-height: 44px;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffffff;
}

sources.sass\\layout\\_header.scss:
.header{
    position: absolute;
    z-index: 100;
    width: 100%;
    left: 50%;
    transform: translateX(-50%);
}

.header-content-wrapper{
    position: relative;
    background-color: transparent;
    min-width: 320px;
    width: 100%;
    font-family: DM Sans;
    color: #ffffff;
    padding-top: 6px;
    @media screen and (min-width: 768px){
        padding-top: 38px;
    }
}

.header-flex-container{
    position: relative;
    display: flex;
    flex-grow: 1;
    @media screen and (min-width: 1280px){
        left: -70px;
        width: calc(100% + 70px);
        display: flex;
    }
}

.header-navigation-wrapper{
    position: relative;
    right: 0;
    display: flex;
    flex-direction: row;
    align-items: center;
    min-height: auto;
    top: unset;
    flex-grow: 1;
    background-color: transparent;
    &.is-open{
        position: absolute;
        right: -20px;
        top: -5px;
        display: flex;
        flex-direction: column;
        width: 254px;
        height: 500px;
        flex-grow: 1;
        background-color: #E17992;
        z-index: 10;
    }

    @media screen and (min-width: 768px){
        &.is-open{
            top: -38px;
            right: -34px;
            height: 100vh;
        }
    }

    @media screen and (min-width: 1280px){
        display: flex;
        position: unset;
        flex-direction: row;
        align-items: center;
        width: auto;
        height: auto;
        flex-grow: 1;
        background-color: transparent;
        &.is-open{
            display: flex;
            position: unset;
            flex-direction: row;
            align-items: center;
            width: auto;
            height: auto;
            flex-grow: 1;
            background-color: transparent;
        }
    }
}



.header-logo{
    margin-right: 0px;
    width: auto;
    @media screen and (min-width: 1280px){
        margin-right: 110px;
    }
    img{
        display: block;
        margin-right: 18px;;
    }
    &-link{
        text-decoration: none;
        display: flex;
        align-items: center;
    }
}

.header-logo-title{
    font-family: Titan One;
    color: #D41443;
    font-size: 25px;
    text-transform: uppercase;
}

.wrapper-header{
    display: flex;
}

.header-main-navigation{
    @media screen and (max-width: 1280px){
        width: 100%;
        order: 0;
        &.is-open{
            order: 1;
        }
    }
}
.header-main-navigation-list{
    display: flex;
    font-weight: bold;
    display: none;
    &.is-open{
        display: flex;
        display: block;
        flex-direction: column;
    }
    @media screen and (min-width: 1280px){
        display: flex;
        width: 100%;
        &.is-open{
            display: flex;
            flex-direction: row;
        }
    }
}

.header-main-navigation-list__item{
    margin-right: 35px;
    cursor: pointer;
    &:last-child{
        margin-right: 0;
    }
    &-link{
        color: #ffffff;
        text-decoration: none;
        &:hover{
            color: #D41443;
        }
    }
    @media screen and (max-width: 1280px){
        padding-left: 24px;
        margin-right: 0;
        padding-top:11px;
        padding-bottom:11px;
        border-top: 1px solid #E18298;
        &:last-child{
            border-bottom: 1px solid #E18298;
        }
    }
}

.header-current-link{
    color: #D41443;
    text-decoration: none;
}

.header-burger-menu{
    margin-right: 0;
    margin-left: 5px;
    position: unset;
    display: block;
    &.is-open{
        position: absolute;
        top: 23px;
        right: 34px;
    }
    @media screen and (min-width: 768px){
        margin-right: 30px;
    }
    @media screen and (min-width: 1281px){
        display: none;
    }

}

.header-burger-button{
    background: none;
    border: none;
    outline: none;

    .icon-close{
        display: none;
    }
    
    &.is-open .icon-close{
        display: block;
    }
    &.is-open .icon-burger{
        display: none;
    }
}

.header-button{
    display: none;    
    margin-top: 0;
    margin-bottom: 0;
    @media screen and (min-width: 768px){
        display: block;
    }
    @media screen and (min-width: 1280px){
        margin-left: auto;
        margin-right: unset;
        margin-top: 0;
        margin-bottom: 0;
    }
    &.is-open{
        display: block;
        margin: 60px auto 19px;
        @media screen and (min-width: 768px){
            display: block;
        }
        @media screen and (min-width: 1280px){
            margin-left: auto;
            margin-right: unset;
            margin-top: 0;
            margin-bottom: 0;
        }
    }

}

sources.sass\\layout\\_hero.scss:
.hero{
    background-color: $hero-bg;
    .button{
        @media screen and (max-width: 1280px){
            height: 24px;
            font-size: 8px;
            line-height: 10px;
            padding-left: 15px;
            padding-right: 15px;
        }
    }
}
 
h1{
    .hero-title-st{
        color: $white;
    }
    .hero-section-title-text{   
        color: $accent;
    }
}
 

 
 
.hero-list{
    list-style: none;
    text-decoration: none;
}
 
.hero-buttons{
    display: flex;
    & li:not(:last-child){
        margin-right: 8px;
    }
}

.hero-ellipce{
    position: absolute;
    z-index: 2;
}

.ice-cream{
    position: absolute;
    z-index: 3;
}

.hero-section{
    padding-right: 20px;
    padding-left: 20px;
    position: relative;
    height: 502px;   
    h1{
        display: inline-block; // ширина заголовка по контенту
    }
}

.hero-button{
    height: 24px;
}

.hero-products-button{
    width: 63px;
    margin-right: 8px;
}

.hero-how-its-made-button{
    width: 83px;
}

@media screen and (max-width: 767px){
    .hero-section{
        overflow: hidden;
 
        .hero-ellipce{
            
            left: 36px;
            top: 83px;
            width: 346px;
            height: 344px;
            background-color: $hero-ellipce;
            border-radius: 50%;
        }
 
        .ice-cream{
            
            left: 117px;
            top: 92px;
            width: 171px;
            height: 410px;
            background-image: url(../images/hero/mob-ice-cream.png);
            
        }
 
        @media screen and (min-device-pixel-ratio: 2),
        screen and (min-resolution: 192dpi),
        screen and (min-resolution: 2dppx){
            .ice-cream{
                background-image: url(../images/hero/mob-ice-cream@2x.png);
                background-size: 100% auto;     //подстраиваю картинку под ширину контейнера
            }
        }
 
 
        h1{
            position: relative;
            z-index: 4;  
            text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
            font-size: 26px;
            line-height: 1.154;
            letter-spacing: 0.04em;
            
            padding-top: 193px;
 
            .hero-section-title-text{   
                font-size: 48px;
                line-height: 96.5%;
            }
        }
        
        .hero-girl,
        .hero-product,
        .hero-numbers{
            display: none;
        }
 
        .hero-buttons{
            position: absolute;
            z-index: 4; 
            margin-top: 19px;
        }
        
       
    }
        
}
 
@media screen and (min-width: 768px){
 
    .hero-section{
        padding-right: 34px;
        padding-left: 34px;
    
        height: 399px;
 
        .hero-title-container{
            display:flex;
            justify-content: space-between;
            padding-top: 102px;
        }
 
        
 
        .hero-buttons{
            
            margin-top: 8px;
        }
   
        
    }
 
    h1{
        font-size: 22px;
        line-height: 1.136; 
        
        
 
        .hero-section-title-text{ 
            font-size: 34px;
            line-height: 90.5%;
        }
    }
 
    .hero-numbers{
     
        text-align: right;
        color: $white;
        font-family: $main-font;
        font-style: normal;
        font-weight: normal;
        font-size: 8px;
        line-height: 10px;
        .hero-num-one{
            margin-bottom: 13px;
            }
        
 
        .hero-num{
            position: relative;
            z-index: 3;
            font-size: 28px;
            line-height: 32px;
            color: $accent;
            font-family: $h-font;
            font-style: normal;
            font-weight: normal;
            &::after{
                    content: "";
                    display: block;
                    position: absolute;
                    width: 16px;
                    height: 16px;
                    right: -8px;
                    top: -2px;
                    border-radius: 16px;
                    background-color: $white;
                    z-index: -1;
            }
        }
 
        
    }
 
    .hero-product{
        position: relative;
        display: block;
        margin-top: 20px;
        
        

        

        .hero-product-description{
            font-family: $main-font;
            font-style: normal;
            font-weight: normal;
            font-size: 8px;
            line-height: 12px;
            color: $white;
            padding-top: 47px;
            padding-left: 120px;
            p{
                display: inline-block;
                width: 114px;
                height: 48px;
                margin-bottom: 7px;
                letter-spacing: 0;
            }
            
        }
    }

    .hero-ellipce{
            
        width: 324px;
        height: 322px;
        left: 307px;
        top: 31px;
        background-color: $hero-ellipce;
        border-radius: 50%;
    }

    .hero-milk-img{
        position: absolute;
        width: 124px;
        height: 126px;
        background-image: url(../images/hero/tab-milk.png);
        
    }
 
    .ice-cream{
            
        width: 143px;
        height: 336px;
        left: 378px;
        top: 63px;
        background-image: url(../images/hero/tab-ice-cream.png);
        
    }

    .hero-girl  {
