sources.sass\\layout\\_feedback.scss:
.feedback{
    padding-bottom: 100px;
    background-color:#FFFFFF;
}

.feedback-container{
    margin: 0 auto;
    padding-left: 20px;
    padding-right: 20px;
}

.feedback-photo {
    margin-left: auto;
    margin-right:auto;
}

.feedback-description {
    position: relative;
    background-color: #FAFAFA;
}

.feedback-text, .feedback-text-name {
    text-align: center;
    align-items: center;
    justify-content: center;
    font-family: DM Sans;
    font-style: normal;
    font-weight: 500;
    font-size: 12px;
    line-height: 2.55;
    color: #000000;
}

.feedback-wrapper-for-text{
    position: relative;
    background-color: transparent;
}

.feedback-text {
    position: relative;
/*     border: 1px solid red; */
    z-index: 1;
    text-decoration-style: solid;
    text-decoration-color: #E1E1E1;
}

.feedback-stripes{
    position: absolute;
    top: 28.5px;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
    to bottom,
    #E1E1E1,#E1E1E1 1px,
    #FAFAFA 1px,#FAFAFA );
    background-size: 100% 30px;
}

.feedback-quotes {
    position: absolute;
    fill: #E1E1E1;
    width: 54px;
    height: 40px;
}

.feedback-circle {
    display: flex; 
    align-items: center;
    justify-content: center;   
}

.feedback-circle-icon {
    border-radius: 50%;
    width: 4px;
    height: 4px;
    background: #C7133E;
    &:not(:last-child) {
        margin-right: 10px;
    }
}

.feedback-icons {
    display: flex;
    justify-content: center;
    align-items: center;
    padding-left: 0px;
    margin-bottom: 0px;
}

.feedback-icons-label:not(:last-child) {
    margin-right: 18px;
}

.feedback-icon-list-svg {
    width: 12px;
    height: 12px;
    fill: #D31342;
}

.feedback-icon-list-background {
    display: block;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #D9D9D9;
}

@media screen and (min-width: 320px) and (max-width:767px) {
    .feedback-photo {
        margin-bottom: 29px;
    }

    .feedback-description {
        margin-left: auto;
        margin-right: auto;
        padding: 43px 66px 26px 57px;
        width: 280px;
        height: 308px;
    }

    .feedback-quotes {
        top: 28px;
        left: 61px;
    }

    .feedback-text {
        letter-spacing: 0;
        border-bottom: unset;
    }

    .feedback-circle {
        margin-top: 30px;
    }

    .feedback-text-name {
        margin-top: 18px;
        font-size: 14px;
        line-height: 1.86;
    }
    .feedback-icons {
        margin-top: 30px;
    }
}

@media screen and (min-width: 768px) {
    .feedback-container {
        width: 768px;
        padding-left: 34px;
        padding-right: 34px;
    }

    .feedback-photo {
        margin-bottom: 19px;
    }

    .feedback-description {
        margin-left: auto;
        margin-right: auto;
        padding: 47px 80px 34px 80px;
        width: 620px;
        height: 164px;
    }

    .feedback-quotes {
        z-index: 1;
        top: 30px;
        left: 40px;
    }

    .feedback-text {
        font-size: 14px;
        line-height: 2.16;
        letter-spacing: 0.04em;
        border-bottom: unset;
    }

    .feedback-circle {
        margin-top: 24px;
    }

    .feedback-text-name {
        margin-top: 14px;
        font-size: 16px;
        line-height: 1.86;
    }

    .feedback-icons {
        margin-top: 35px;
    }
}


@media screen and (min-width: 1280px) {
    .feedback-container {
        width: 1300px;
        padding-left: 115px;
        padding-right: 115px;
    }

    .feedback-description {
        margin-left: auto;
        margin-right: auto;
        padding: 47px 88px 39px 88px;
        width: 926px;
        height: 138px;
    }

    .feedback-quotes {
        top: 32px;
        left: 50px;
    }

    .feedback-text {
        font-size: 16px;
        line-height: 1.86;
        border-bottom: 1px solid #E1E1E1;
    }

    .feedback-circle {
        margin-top: 15px;
    }

    .feedback-text-name {
        margin-top:15px;
        font-size: 20px;
        line-height: 1.86;
    }

    .feedback-photo {
        margin-bottom: 15px;
    }

    .feedback-icons {
        margin-top: 35px;
    }
}

sources.sass\\layout\\_footer.scss:
footer {
  background-color: #fff5f6;
}

.footer {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  padding: 97px 0;
}

.footer-text-rose {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 30px;
  line-height: 1.133;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffa5ba;
  margin-bottom: 15px;
}
.footer-text-red {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 58px;
  line-height: 1.137;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  color: #d41443;
}
.footer-tel {
  margin-top: 50px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  letter-spacing: 0.02em;
  color: #000000;
  a {
    color: #d41443;
    text-decoration: none;
  }
}
.footer-social-icons ul {
  list-style: none;
  display: flex;
  padding: 0;
  justify-content: flex-end;
  li:not(:last-child) {
    margin-right: 24px;
  }
  a {
    border-radius: 50%;
    background: #fea5bb;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 34px;
    height: 34px;
    transition: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 250ms;
    svg {
      fill: #fff;
    }
    &:hover {
      background: #d41443;
    }
  }
}
.footer-copyright {
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  letter-spacing: 0.02em;
  color: #907e82;
  text-align: center;
  border-top: 1px solid #e1e1e1;
  padding: 10px 0;
}

@media screen and (max-width: 767px) {
  .footer {
    flex-direction: column;
    padding: 113px 0 0 0;
  }
  .footer-text {
    padding: 0;
  }

  .footer-text-rose {
    padding-right: 66px;
    font-size: 18px;
    line-height: 1.166;
  }
  .footer-text-red {
    padding-bottom: 43px;
    font-size: 34px;
    line-height: 1.147;
    letter-spacing: 0.04em;
    span {
      display: block;
    }
  }
  .footer-social-icons {
    width: 100%;
    ul {
      li:not(:last-child) {
        margin-right: 28.24px;
      }
      a {
        width: 32px;
        height: 32px;
      }
    }
  }

  .footer-tel {
    font-size: 12px;
    padding-bottom: 43px;
    text-align:right;
  }
  .footer-copyright {
    padding: 22px 0 25px 0;
    span{
      display: block;
    }
  }
}

@media screen and (min-width: 768px) and (max-width: 1279px){

.footer {
  padding: 91px 0;
    align-items: start;
  }
  .footer-text-rose {
    font-size: 26px;
    line-height: 1.153;
    letter-spacing: 0.04em;
  }
  .footer-text-red {
    font-size: 48px;
    line-height:1.145;
    letter-spacing: 0.06em;
        span {
      display: block;
    }
    
  }
  .footer-tel {
    font-size: 14px;
   }
  .footer-copyright{
    padding-top: 29px;
    padding-bottom: 18px;
  } 
}
